<?php/* * Created on 30.05.2006 * * Author: Mats * * File: Language.php * */class Language{	private $languageArray;	private $numberOfLanguages;	private  $defaultlanguage;	private static $instance;	public static function getInstance()	{		if (!isset(self::$instance))		{            self::$instance = new Language();        }        return self::$instance;	}	private function Language()	{		$languageArray=array();		$this->languageArray[1]['short']='de';		$this->languageArray[2]['short']='en';        $this->languageArray[3]['short']='es';        $this->languageArray[4]['short']='ch';        $this->languageArray[1]['Long']='';		$this->languageArray[2]['Long']='english/';        $this->languageArray[3]['Long']='test/';         $this->languageArray[4]['Long']='test2/';		$this->languageArray[1]['Name']='deutsch';		$this->languageArray[2]['Name']='english';        $this->languageArray[3]['Name']='Test';        $this->languageArray[4]['Name']='Test2';		$this->numberOfLanguages =2;		$this->defaultlanguage = 1;	}	function getLanguageArray()	{		return $this->languageArray;	}	function getNumberOfLanguages()	{		return $this->numberOfLanguages;	}	function getDefaultLanguage()	{		return $this->defaultlanguage;	}	function translateLanguage($lang)	{		for ($counter=1; $counter <= count($this->languageArray); $counter++)		{			if ($this->languageArray[$counter]['Name']==$lang)				return $counter;		}		return $this->defaultlanguage;	}	function renderLanguageTabs($pathToTemplate)	{		$returnvalue="";		if ($this->getNumberOfLanguages()>1)		{			$returnvalue.="<div id=\"tabulatorenfeld_langs\">					<div id=\"tabs_langs\">					<ul id=\"tab_ul\">";			for ($counter=1; $counter<=$this->getNumberOfLanguages(); $counter++)			{				$returnvalue.="<li><a href=\"#\" id=\"langtab".$counter."\"";				if ($counter==1)					$returnvalue.=" style=\"background:url(../godot/backEnd/images_system/right/tab_images/lang_tab_bg_sel.gif) no-repeat;\""."\r\n";				$returnvalue.=" onclick=\"MM_showHideLayers(";				for ($counter2=1; $counter2<=$this->getNumberOfLanguages(); $counter2++)				{					$returnvalue.="'LangLayer".$counter2."','','";					if ($counter==$counter2)						$returnvalue.= "show";					else						$returnvalue.="hide";					$returnvalue.="'";					if ($counter2<$this->getNumberOfLanguages())						$returnvalue.=",";				}				$returnvalue.=")\" onmouseup=\"MM_changeButtonStateLang(";				for ($counter2=1; $counter2<=$this->getNumberOfLanguages(); $counter2++)				{					$returnvalue.="'langtab".$counter2."','','";					if ($counter==$counter2)						$returnvalue.= "show";					else						$returnvalue.="hide";					$returnvalue.="'";					if ($counter2<$this->getNumberOfLanguages())						$returnvalue.=",";				}				$returnvalue.=")\">";				$returnvalue.="<div class=\"tab_inner\">".$this->languageArray[$counter]['Name']."</div></a></li>"."\n";			}			$returnvalue.="</ul>							</div>						</div>"."\n";		}		for ($counter=1; $counter<=$this->getNumberOfLanguages(); $counter++)		{			$tags=array('Langshort'=> $this->languageArray[$counter]['short'],						'LangID'=> $counter						);			$tp=&new templateParser(PATH.$pathToTemplate);			$tp->parseTemplate($tags);			$returnvalue.=$tp->display();		}		return $returnvalue;	}}?>